-- Bitwise operations for Lua 5.1 (simplified implementations)
function caml_int_and(a, b)
  -- Simplified bitwise AND for common cases
  -- For full implementation, see runtime/lua/ints.lua
  local result, bit = 0, 1
  a = math.floor(a)
  b = math.floor(b)
  while a > 0 and b > 0 do
    if a % 2 == 1 and b % 2 == 1 then
      result = result + bit
    end
    a = math.floor(a / 2)
    b = math.floor(b / 2)
    bit = bit * 2
  end
  return result
end
function caml_int_or(a, b)
  local result, bit = 0, 1
  a = math.floor(a)
  b = math.floor(b)
  while a > 0 or b > 0 do
    if a % 2 == 1 or b % 2 == 1 then
      result = result + bit
    end
    a = math.floor(a / 2)
    b = math.floor(b / 2)
    bit = bit * 2
  end
  return result
end
function caml_int_xor(a, b)
  local result, bit = 0, 1
  a = math.floor(a)
  b = math.floor(b)
  while a > 0 or b > 0 do
    local a_bit, b_bit = a % 2, b % 2
    if a_bit ~= b_bit then
      result = result + bit
    end
    a = math.floor(a / 2)
    b = math.floor(b / 2)
    bit = bit * 2
  end
  return result
end
