    if result1.tag == 0 then
      return -1  -- Ok < Error
    else
      return 1   -- Error > Ok
    end
  end
  if result1.tag == 0 then
    return ok_cmp(result1[1], result2[1])
  else
    return error_cmp(result1[1], result2[1])
  end
end


