--Provides: caml_result_to_seq
function caml_result_to_seq(result)
  if result.tag == 1 then
    return function() return 0 end  -- Empty sequence
  end
  local yielded = false
  return function()
    if yielded then
      return 0
    end
    yielded = true
    return {tag = 0, result[1], function() return 0 end}
  end
end


