--Provides: caml_float_compare
function caml_float_compare(x, y)
  -- OCaml-style comparison: NaN = NaN, NaN < other values
  if x ~= x and y ~= y then
    return 0  -- NaN = NaN
  end
  if x ~= x then
    return -1  -- NaN < y
  end
  if y ~= y then
    return 1  -- x > NaN
  end
  if x < y then
    return -1
  end
  if x > y then
    return 1
  end
  return 0
end

