  if type(exc) ~= "table" then
    return "Unknown"
  end
  if exc.tag == 248 and type(exc[1]) == "string" then
    return exc[1]
  end
  if exc.tag == 0 and type(exc[1]) == "table" and exc[1].tag == 248 then
    return exc[1][1] or "Unknown"
  end
  return "Unknown"
end

