  }
end

--Provides: caml_stream_of_array
--Requires: caml_stream_from
function caml_stream_of_array(arr)
  local len = arr[0]
  local pos = 1

  local function generator()
    if pos > len then
      return nil
    end
    local value = arr[pos]
    pos = pos + 1
    return value
  end

  return caml_stream_from(generator)
