    if 1/v == -math.huge then
      lo, hi = 0, 0x80000000
    else
      lo, hi = 0, 0
    end
  elseif v ~= v then
    lo, hi = 0x00000001, 0x7ff00000
  elseif v == math.huge then
    lo, hi = 0, 0x7ff00000
  elseif v == -math.huge then
    lo, hi = 0, 0xfff00000
  else
    local sign = v < 0 and 1 or 0
    v = math.abs(v)

    local exp = math.floor(math.log(v) / math.log(2))
    local frac = v / (2 ^ exp) - 1

    exp = exp + 1023
    if exp <= 0 then
      exp = 0
      frac = v / (2 ^ -1022)
    elseif exp >= 0x7ff then
      exp = 0x7ff
      frac = 0
