    local frac_hi = math.floor(frac * (2 ^ 20))
    local frac_lo = math.floor((frac * (2 ^ 52)) % (2 ^ 32))

    hi = caml_hash_bit_or(caml_hash_bit_lshift(sign, 31), caml_hash_bit_or(caml_hash_bit_lshift(exp, 20), frac_hi))
    lo = frac_lo
