  local ofs = 0
  -- LAYOUT: C_LAYOUT=0, FORTRAN_LAYOUT=1

  if ba.layout == 0 then
    -- C layout: row-major, 0-indexed
    for i = 1, #ba.dims do
      local idx = indices[i]
      if idx < 0 or idx >= ba.dims[i] then
