  if #ba.dims ~= 1 then
    error("Bigarray.sub: only for 1D arrays")
  end

  local elem_size = caml_ba_get_size_per_element(ba.kind)

  -- Create shallow copy of data starting at offset
  local new_data = {}
  local start_ofs = ofs * elem_size + 1
  for i = 0, len * elem_size - 1 do
