  -- Calculate total length
  local total_len = 0
  local l = list
  while l ~= 0 do
    local arr = l[1]  -- head
    total_len = total_len + arr[0]
    l = l[2]  -- tail
  end

  -- Build result
  local result = { tag = 0, [0] = total_len }
  local pos = 1

  l = list
  while l ~= 0 do
    local arr = l[1]
    local arr_len = arr[0]
    for i = 1, arr_len do
      result[pos] = arr[i]
      pos = pos + 1
    end
    l = l[2]
  end

  return result
end

